function get_data(n,e){n.forEach(n=>{const s=n.Genre.split(",");s.includes(e)&&(console.log(s),main_slider.innerHTML+=`\n        <div class="main-slider-box">\n                <!-- Overlayer -->\n                <span onclick="linkFilm(${n.id})" class="main-slider-overlayer">\n                    <img src="src/Images/bouton-jouer.png" alt="">\n                </span>\n\n                <!-- img -->\n                <div class="main-slider-img">\n                    <img src="${n.Poster}" alt="">\n                </div>\n                <!-- text -->\n                <div class="main-slider-text">\n                    <span class="quality">Full HD</span>\n                    <div class="bottom-text">\n                        <div class="movie-name">\n                            <span>${n.Year}</span>    \n                            <strong>${n.Title}</strong>\n                        </div>\n                        <div class="category-rating">\n                            <div class="category">\n                                <p>${n.Genre}</p>\n                            </div>\n                            <div class="rating" style="padding-left: 5px;">\n                                ${n.imdbRating} <img src="src/Images/IMDb-icon.png" alt="">\n                            </div>\n                        </div>\n                    </div>\n                </div>\n        </div>\n                        \n        `)})}const slc_genre=document.getElementById("genre-select");show_genre=(n=>{const e=n.map(n=>n.Genre.split(",")),s=e.flat(),t=[...new Set(s)];t.forEach(n=>{slc_genre.innerHTML+=`<option value="${n}">${n.trim()}</option>`})}),slc_genre.addEventListener("change",()=>{const n=slc_genre.value;main_slider.innerHTML="",fetch("jsonformatter.json").then(n=>n.json()).then(e=>get_data(e,n)).catch(n=>console.log(n))}),fetch("jsonformatter.json").then(n=>n.json()).then(n=>show_genre(n)).catch(n=>console.log(n));