function get_data(n){n.forEach(n=>{main_slider.innerHTML+=`\n        <div class="main-slider-box">\n                <!-- Overlayer -->\n                <span onclick="linkFilm(${n.id})" class="main-slider-overlayer">\n                    <img src="src/Images/bouton-jouer.png" alt="">\n                </span>\n\n                <!-- img -->\n                <div class="main-slider-img">\n                    <img src="${n.Poster}" alt="">\n                </div>\n                <!-- text -->\n                <div class="main-slider-text">\n                    <span class="quality">Full HD</span>\n                    <div class="bottom-text">\n                        <div class="movie-name">\n                            <span>${n.Year}</span>    \n                            <strong>${n.Title}</strong>\n                        </div>\n                        <div class="category-rating">\n                            <div class="category">\n                                <p>${n.Genre}</p>\n                            </div>\n                            <div class="rating" style="padding-left: 5px;">\n                                ${n.imdbRating} <img src="src/Images/IMDb-icon.png" alt="">\n                            </div>\n                        </div>\n                    </div>\n                </div>\n        </div>\n                        \n        `})}function more_info(n){main_slider.style.display="none"}function linkFilm(n){window.location.href=`movie-page.html?film=${n}`}let container=document.getElementById("container"),main_slider=document.getElementById("main-slider");const navLinks=document.querySelectorAll(".navbar .container .nav-item .nav-link");let all_data_film=[],xhr=new XMLHttpRequest;xhr.onreadystatechange=function(){if(4==this.readyState&&200==this.status){let n=xhr.responseText;all_data_film=n,n=JSON.parse(n),get_data(n)}},xhr.open("GET","jsonformatter.json",!0),xhr.send();