function get_data(n){n.forEach(n=>{n.id==id_film&&(document.title=n.Title,container.innerHTML=`\n                <div class="baner-movie">\n                  <div class="baner-img">\n                    <img src="${n.Cover}">\n                  </div>\n                  <div class="baner-text">\n                    <div class="baner-text-top">\n                      <h2>${n.Title}</h2>\n                      <div class="baner-text-content">\n                        <p class="quality">Full HD</p>\n                        <div class="rating">\n                          ${n.imdbRating} <img src="src/Images/IMDb-icon.png" alt="">\n                        </div>\n                        <p>${n.Released}</p>\n                        <p class="mins">${n.Runtime}</p>\n                      </div>\n                    </div>\n                    <div class="baner-text-bottom d-flex">\n                      <div class="baner-bottom">\n                        <button class="btn-watch"><a href="#m-video">Watch trailer</a></button>\n                      </div>\n\n                      <div class="baner-bottom ms-4">\n                        <button class="btn-watch"><a href="#m-play">play</a></button>\n                      </div>\n                    </div>\n                  </div>\n                </div>\n                <!--=Video =================================================================-->\n                \n                <p class="play-title">trailer</p>\n                <div class="play">\n                  <div class="play-movie">\n                    <video id="m-video" controls autoplay>\n                      <source src="${n.src}">\n                    </video>\n                  </div>\n                </div>\n                \n              </div>\n              <!--=Download ==========================================================================-->\n              <div class="download container">\n                <p class="download-titla">Download</p>\n                <table class="table table-dark table-hover">\n                  <tbody>\n                    <tr>\n                      <td>Server 1</td>\n                      <td>${n.Language}</td>\n                      <td>480p</td>\n                      <td><a href="#">Download</a></td>\n                    </tr>\n                    <tr>\n                      <td>Server 2</td>\n                      <td>${n.Language}</td>\n                      <td>720p</td>\n                      <td><a href="#">Download</a></td>\n                    </tr>\n                    <tr>\n                      <td>Server 3</td>\n                      <td>${n.Language}</td>\n                      <td>1080p</td>\n                      <td><a href="#">Download</a></td>\n                    </tr>\n                  </tbody>\n                </table>\n                \n                <p class="play-title">Server</p>\n                <div class="play">\n                  <div class="play-movie">\n                    <video id="m-play" controls>\n                      <source src="${n.src}">\n                    </video>\n                  </div>\n                </div>\n                \n                `)})}let container=document.getElementById("container");const urlParams=new URLSearchParams(window.location.search),id_film=urlParams.get("film");let xhr=new XMLHttpRequest;xhr.onreadystatechange=function(){if(4==this.readyState&&200==this.status){let n=xhr.responseText;n=JSON.parse(n),get_data(n)}},xhr.open("GET","jsonformatter.json",!0),xhr.send();